import React from 'react';
import { useLocation } from 'react-router-dom';
import Header from '../components/Header';

const ProductPage = () => {
  // Use useLocation hook to access location object
  const location = useLocation();
  // Extract product data from location state
  const product = location.state && location.state.product;

  // Check if product data exists
  if (!product) {
    console.log('No product data found:', location.state); // Log location state
    return <div>No product data found</div>;
  }

  // Log the product data to ensure it's correct
  console.log('Product data:', product);

  // Render product details
  return (
    <div>
      <Header />
      <div className="container mx-auto mt-8">
        <div className="bg-white p-8 rounded-lg shadow-md flex items-center">
          {/* Product Image */}
          <div className="mr-4">
            <img src={product.productImage} alt={product.productName} className="h-250 w-100 rounded-lg" />
          </div>

          {/* Product Details */}
          <div>
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-xl font-semibold">{product.productName}</h2>
            </div>
            <div className="text-gray-600">
              Category 1 {/* Replace with actual category */}
            </div>
            <div className="mb-4">
              <p className="text-gray-700">Product Details</p>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-gray-600">Price: {product.productPrice}</span>
              <button className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600">Add to Cart</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
