import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Header from '../components/Header';
import Footer from '../components/Footer';
import { useParams } from 'react-router-dom';

const ProductPage = () => {
  const { productId } = useParams();  // Use useParams to extract productId
  const [productData, setProductData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchProductDetails = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/search/${productId}`);
      if (response.data && response.data.length > 0) {
        setProductData(response.data[0]);
      } else {
        setError('No product data found');
      }
      setLoading(false);
    } catch (error) {
      setError('Error fetching product details');
      setLoading(false);
    }
  };

  useEffect(() => {
    if (productId) {
      fetchProductDetails();
    }
  }, [productId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  if (!productData) {
    return <div>No product data found.</div>;
  }

  return (
    <div>
      <Header />
      <div className="container mx-auto mt-10">
        <div className="bg-white p-8 rounded-lg shadow-md flex items-center space-x-4">
          {/* Product Image */}
          <div className="flex-shrink-0">
            <img
              src={productData.productImage}
              alt={productData.productName}
              className="rounded-lg"
              style={{ height: '300px', width: '300px', objectFit: 'cover' }}
            />
          </div>

          {/* Product Details */}
          <div className="flex flex-col space-y-4">
            <h2 className="text-xl font-semibold">{productData.productName}</h2>
            <div className="text-gray-600">Category: {productData.productCategory}</div>
            <div className="text-gray-700">{productData.productDescription}</div>
            <div className="text-gray-600">Seller: {productData.productSeller}</div>
            <div className="text-gray-600">Stock: {productData.productStock}</div>
            <div className="text-gray-600">Price: ${productData.productPrice}</div>
            <button className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600">
              Add to Cart
            </button>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default ProductPage;
